                        {user?.seller?.profile_image ?
                            <img src={user?.seller?.profile_image} style={{ width: '60px', height: '60px', borderRadius: '100%' }} />
                            :
                            <img src={blankUser} style={{ width: '60px', height: '60px', borderRadius: '100%' }} />
                        }
